mapper.select:
select
deptId,parentId,name,orderNum,delFlag from sys_dept
where deptId = #{value}

select deptId,parentId,name,orderNum,delFlag from
sys_dept

	 and deptId = #{deptId} 
	 and parentId = #{parentId} 
	 and name = #{name} 
	 and orderNum = #{orderNum} 
	 and delFlag = #{delFlag} 



		order by ${sort} ${order}


		order by deptId desc



		limit #{offset}, #{limit}

select count(*) from sys_dept

	 and deptId = #{deptId} 
	 and parentId = #{parentId} 
	 and name = #{name} 
	 and orderNum = #{orderNum} 
	 and delFlag = #{delFlag}

mapper.update:
update sys_dept

	parentId = #{parentId}, 
	name = #{name}, 
	orderNum = #{orderNum}, 
	delFlag = #{delFlag}

where deptId = #{deptId}
